// src/components/Review.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Review = () => {
    const [applications, setApplications] = useState([]);
    const [selectedApplication, setSelectedApplication] = useState(null);
    const [remark, setRemark] = useState('');
    const [status, setStatus] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true); // Loading state

    useEffect(() => {
        const fetchApplications = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/applications', {
                    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
                });
                setApplications(response.data);
            } catch (err) {
                setError('Error fetching applications');
            } finally {
                setLoading(false); // Set loading to false after fetching
            }
        };

        fetchApplications();
    }, []);

    const handleReview = async (applicationId) => {
        try {
            await axios.put(`http://localhost:5000/api/applications/${applicationId}/review`, {
                approved: status === 'approved',
                remark
            }, {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            // Refresh applications after submitting review
            const updatedApplications = applications.map(app => 
                app._id === applicationId ? { ...app, status } : app
            );
            setApplications(updatedApplications);
            // Clear the form
            setRemark('');
            setStatus('');
            setSelectedApplication(null);
        } catch (err) {
            setError('Error submitting review');
        }
    };

    if (loading) {
        return <p>Loading applications...</p>; // Loading message
    }

    return (
        <div>
            <h1>Review Applications</h1>
            {applications.map(app => (
                <div key={app._id}>
                    <h3>Application ID: {app._id}</h3>
                    <button onClick={() => setSelectedApplication(app)}>Review</button>
                </div>
            ))}
            {selectedApplication && (
                <div>
                    <h2>Review Application ID: {selectedApplication._id}</h2>
                    <textarea 
                        value={remark} 
                        onChange={(e) => setRemark(e.target.value)} 
                        placeholder="Enter remark" 
                    />
                    <select onChange={(e) => setStatus(e.target.value)} value={status}>
                        <option value="">Select Status</option>
                        <option value="approved">Approve</option>
                        <option value="rejected">Reject</option>
                    </select>
                    <button onClick={() => handleReview(selectedApplication._id)}>Submit Review</button>
                </div>
            )}
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
};

export default Review;
