import React, { useState } from 'react';
import axios from 'axios';

const ApplicationForm = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [resume, setResume] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();

        const token = localStorage.getItem('token'); // Adjust this based on your implementation

        // Prepare your application data
        const applicationData = {
            name,
            email,
            resume,
        };

        console.log('Submitting application data:', applicationData); // Log the data

        try {
            const response = await axios.post('http://localhost:5000/api/applications', applicationData, {
                headers: {
                    'Authorization': `Bearer ${token}` // Include the token in the headers
                }
            });

            console.log('Application submitted successfully:', response.data);
        } catch (error) {
            console.error('Error submitting application:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Name:</label>
                <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
            </div>
            <div>
                <label>Email:</label>
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
            </div>
            <div>
                <label>Resume:</label>
                <input type="text" value={resume} onChange={(e) => setResume(e.target.value)} required />
            </div>
            <button type="submit">Submit Application</button>
        </form>
    );
};

export default ApplicationForm;
