// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import Dashboard from './components/Dashboard';
import ApplicationForm from './components/ApplicationForm';
import Review from './components/Review';
import UserProfile from './components/UserProfile';
import Notifications from './components/Notifications';
import JobSearch from './components/JobSearch';
import ResumeUpload from './components/ResumeUpload';
import PrivateRoute from './components/PrivateRoute'; // Ensure this file exists

const App = () => {
    return (
        <Router>
            <div className="App">
                <h1>Job Application Portal</h1>
                <Routes>
                    <Route path="/" element={<Login />} />
                    <Route path="/register" element={<Register />} />
                    <Route path="/dashboard" element={<PrivateRoute component={Dashboard} />} />
                    <Route path="/apply" element={<PrivateRoute component={ApplicationForm} />} />
                    <Route path="/review" element={<PrivateRoute component={Review} />} />
                    <Route path="/profile" element={<PrivateRoute component={UserProfile} />} />
                    <Route path="/notifications" element={<PrivateRoute component={Notifications} />} />
                    <Route path="/job-search" element={<PrivateRoute component={JobSearch} />} />
                    <Route path="/upload-resume" element={<PrivateRoute component={ResumeUpload} />} />
                    <Route path="*" element={<NotFound />} /> {/* Updated to use a NotFound component */}
                </Routes>
            </div>
        </Router>
    );
};

// NotFound component for 404 errors
const NotFound = () => {
    return (
        <div>
            <h2>404 Not Found</h2>
            <p>The page you are looking for does not exist.</p>
        </div>
    );
};

export default App;
