import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Review = () => {
  const [applications, setApplications] = useState([]);
  const [selectedApplication, setSelectedApplication] = useState(null);
  const [remark, setRemark] = useState('');
  const [status, setStatus] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [submitting, setSubmitting] = useState(false); // State for review submission
  const navigate = useNavigate();

  // Fetch applications when the component mounts
  useEffect(() => {
    const fetchApplications = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/applications', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } // Include token for authorization
        });
        setApplications(response.data);
      } catch (err) {
        setError('Error fetching applications'); // Set error message
        console.error('Error fetching applications:', err);
      } finally {
        setLoading(false); // Set loading to false after fetching
      }
    };

    fetchApplications();
  }, []);

  // Handle the review submission


  // Loading state
  if (loading) {
    return <div>Loading...</div>;
  }

  // Error state
  if (error) {
    return <div style={{ color: 'red' }}>{error}</div>;
  }

  return (
    <div>
      <h1>Review Applications</h1>
      <ul>
        {applications.map(application => (
          <li key={application._id}>
            Application ID: {application._id} - Status: {application.status}
            <button onClick={() => setSelectedApplication(application)}>Review</button>
          </li>
        ))}
      </ul>

      {selectedApplication && (
        <div>
          <h2>Review Application ID: {selectedApplication._id}</h2>
          <textarea 
            placeholder="Enter remark" 
            value={remark} 
            onChange={(e) => setRemark(e.target.value)} 
          />
          <select onChange={(e) => setStatus(e.target.value)} value={status}>
            <option value="">Select Status</option>
            <option value="approved">Approve</option>
            <option value="rejected">Reject</option>
          </select>
          <button onClick={() => handleReview(selectedApplication._id)} disabled={submitting}>
            {submitting ? 'Submitting...' : 'Submit Review'}
          </button>
        </div>
      )}
    </div>
  );
};

export default Review;
