import React, { useState } from 'react';
import axios from 'axios';

const Apply = () => {
  const [resume, setResume] = useState(null); // State to hold the resume file
  const [loading, setLoading] = useState(false); // State to manage loading status
  const [error, setError] = useState(null); // State to manage error messages
  const [success, setSuccess] = useState(null); // State to manage success messages

  // Handle file input change
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const validTypes = ['application/pdf', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'];
      if (!validTypes.includes(file.type)) {
        setError('Please upload a valid PDF or DOCX file.');
        return;
      }
      setResume(file);
      setError(null); // Clear any previous error
    }
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent the default form submission

    if (!resume) {
      console.error('No resume file selected'); // Log if no file is selected
      return;
    }

    const formData = new FormData(); // Create a new FormData object
    formData.append('resume', resume); // Append the resume file to FormData

    // Check if token exists
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No authorization token found. Please log in.');
      return;
    }

    setLoading(true); // Set loading to true
    setError(null); // Reset error state
    setSuccess(null); // Reset success state

    try {
      await axios.post('http://localhost:5000/api/applications', formData, {
        headers: {
          'Content-Type': 'multipart/form-data', // Set the content type for file upload
          Authorization: `Bearer ${token}` // Correctly formatted Authorization header
        }
      });
      // Handle success (e.g., show a success message)
      setSuccess('Application submitted successfully!');
      setResume(null); // Clear the resume file after successful submission
    } catch (error) {
      console.error('Application submission failed', error.response ? error.response.data : error.message); // Log any errors
      setError('Application submission failed. Please try again.'); // Set error message
    } finally {
      setLoading(false); // Set loading to false
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="resume">Upload your resume:</label>
        <input
          id="resume"
          type="file"
          onChange={handleFileChange} // Set the resume file on change
          required // Make this field required
        />
        <button type="submit" disabled={loading}>Submit Application</button>
      </form>
      {loading && <p>Submitting your application...</p>} {/* Loading message */}
      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Error message */}
      {success && <p style={{ color: 'green' }}>{success}</p>} {/* Success message */}
    </div>
  );
};

export default Apply;
