import React from 'react';
import axios from 'axios';

const Apply = () => {
  const handleSubmit = async (resume) => {
    try {
      await axios.post('http://localhost:5000/api/applications', { resume }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } // Corrected line
      });
      // Handle success
    } catch (error) {
      // Handle error
      console.error('Error submitting application:', error);
    }
  };

  return (
    <div>
      {/* Your form and other components go here */}
    </div>
  );
};

export default Apply;
