// src/components/Review.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Review = () => {
    const [applications, setApplications] = useState([]);

    useEffect(() => {
        const fetchApplications = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/applications'); // Adjust the endpoint as needed
                setApplications(response.data);
            } catch (error) {
                console.error('Error fetching applications:', error);
            }
        };

        fetchApplications();
    }, []);

    const handleApprove = async (applicationId) => {
        try {
            // Make an API call to approve the application
            await axios.put(`http://localhost:5000/api/applications/${applicationId}/approve`); // Adjust the endpoint as needed
            // Remove the application from the state
            setApplications(applications.filter(app => app._id !== applicationId));
        } catch (error) {
            console.error('Error approving application:', error);
        }
    };

    const handleReject = async (applicationId) => {
        try {
            // Make an API call to reject the application
            await axios.put(`http://localhost:5000/api/applications/${applicationId}/reject`); // Adjust the endpoint as needed
            // Remove the application from the state
            setApplications(applications.filter(app => app._id !== applicationId));
        } catch (error) {
            console.error('Error rejecting application:', error);
        }
    };

    return (
        <div>
            <h1>Application Review</h1>
            <ul>
                {applications.length > 0 ? (
                    applications.map((application) => (
                        <li key={application._id}>
                            <p>{application.details}</p>
                            <button onClick={() => handleApprove(application._id)}>Approve</button>
                            <button onClick={() => handleReject(application._id)}>Reject</button>
                        </li>
                    ))
                ) : (
                    <li>No applications to review</li>
                )}
            </ul>
        </div>
    );
};

export default Review;
