// src/components/ApplicationForm.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

const ApplicationForm = () => {
    const [details, setDetails] = useState('');
    const [resume, setResume] = useState(null);
    const [error, setError] = useState('');
    const navigate = useNavigate(); // Initialize useNavigate

    const handleSubmit = async (event) => {
        event.preventDefault();
    
        // Get the token from local storage or wherever you store it
        const token = localStorage.getItem('token'); // Adjust this based on your implementation
    
        try {
            const response = await axios.post('http://localhost:5000/api/applications', {
                // Your application data here
            }, {
                headers: {
                    'Authorization': `Bearer ${token}` // Include the token in the headers
                }
            });
    
            console.log('Application submitted successfully:', response.data);
        } catch (error) {
            console.error('Error submitting application:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Submit Application</h2>
            <textarea 
                placeholder="Details" 
                onChange={(e) => setDetails(e.target.value)} 
                required 
            />
            <input 
                type="file" 
                onChange={(e) => setResume(e.target.files[0])} 
                required 
            />
            <button type="submit">Submit</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </form>
    );
};

export default ApplicationForm;
