// src/components/ApplicationForm.js
import React, { useState } from 'react';
import axios from 'axios';

const ApplicationForm = () => {
    const [details, setDetails] = useState('');
    const [resume, setResume] = useState(null);
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('details', details);
        formData.append('resume', resume);

        try {
            await axios.post('http://localhost:5000/api/applications', formData, {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            // Handle success (e.g., redirect or show a success message)
        } catch (err) {
            setError('Application submission failed');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Submit Application</h2>
            <textarea placeholder="Details" onChange={(e) => setDetails(e.target.value)} required />
            <input type="file" onChange={(e) => setResume(e.target.files[0])} required />
            <button type="submit">Submit</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </form>
    );
};

export default ApplicationForm;
