import React, { useState } from 'react';
import axios from 'axios';

const Apply = () => {
  const [resume, setResume] = useState(null); // Use null to indicate no file selected

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!resume) {
      console.error('No resume file selected');
      return;
    }

    const formData = new FormData();
    formData.append('resume', resume); // Append the resume file to FormData

    try {
      await axios.post('http://localhost:5000/api/applications', formData, {
        headers: {
          'Content-Type': 'multipart/form-data', // Set the content type for file upload
          Authorization: `Bearer ${localStorage.getItem('token')}` // Correctly formatted Authorization header
        }
      });
      // Handle success (e.g., show a success message)
      console.log('Application submitted successfully');
    } catch (error) {
      console.error('Application submission failed', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="file"
        onChange={(e) => setResume(e.target.files[0])}
        required
      />
      <button type="submit">Submit Application</button>
    </form>
  );
};

export default Apply;
