// src/components/ApproverDashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ApproverDashboard = ({ token }) => {
    const [applications, setApplications] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchApplications = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/applications/approved', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setApplications(response.data);
            } catch (error) {
                console.error('Error fetching approved applications:', error);
                setError('Failed to load approved applications.');
            }
        };

        fetchApplications();
    }, [token]);

    return (
        <div>
            <h1>Approver Dashboard</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {applications.length > 0 ? (
                applications.map(app => (
                    <div key={app._id}>
                        <h3>Application ID: {app._id}</h3>
                        <p>{app.details}</p>
                        {/* Add approve/reject functionality here */}
                        <button onClick={() => {/* Add approve logic */}}>Approve</button>
                        <button onClick={() => {/* Add reject logic */}}>Reject</button>
                    </div>
                ))
            ) : (
                <p>No approved applications to review.</p>
            )}
        </div>
    );
};

export default ApproverDashboard;
