// src/components/Auth/Login.js
import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/auth/login', { email, password });
            const { token } = response.data; // Assuming the token is returned in the response
            localStorage.setItem('token', token); // Store the token in local storage
            console.log('Login successful:', response.data);
            // Optionally, redirect to the dashboard or another page
            // window.location.href = '/dashboard'; // Uncomment if you want to redirect
        } catch (error) {
            console.error('Login error:', error.response.data);
            setErrorMessage('Invalid email or password'); // Set an error message to display
        }
    };

    return (
        <div>
            <form onSubmit={handleLogin}>
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Email"
                    required
                />
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Password"
                    required
                />
                <button type="submit">Login</button>
            </form>
            {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>} {/* Display error message */}
        </div>
    );
};

export default Login;
