// src/ProtectedRoute.js
import React from 'react';
import { Navigate } from 'react-router-dom';

const ProtectedRoute = ({ children, initiatorId, requiredId, userRole }) => {
    // Check if the user is an initiator or has the correct role
    if (userRole === 'reviewer' || userRole === 'approver') {
        return <Navigate to="/login" />; // Redirect to login if not authorized
    }
    return initiatorId === requiredId ? children : <Navigate to="/login" />;
};

export default ProtectedRoute;
