// src/components/Register.js
import React, { useState } from 'react';
import axios from 'axios';

const Register = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('initiator');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Log the data being sent
            console.log({ email, password, role });

            // Send registration request to the server
            await axios.post('http://localhost:5000/api/auth/register', { email, password, role });
            
            // Redirect to login or dashboard (you can use react-router for navigation)
            // For example: history.push('/login');
        } catch (err) {
            console.error(err.response.data); // Log the error response for debugging
            setError(err.response.data.message || 'Registration failed');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Register</h2>
            <input
                type="email"
                placeholder="Email"
                onChange={(e) => setEmail(e.target.value)}
                required
            />
            <input
                type="password"
                placeholder="Password"
                onChange={(e) => setPassword(e.target.value)}
                required
            />
            <select onChange={(e) => setRole(e.target.value)} value={role}>
                <option value="initiator">Initiator</option>
                <option value="reviewer">Reviewer</option>
                <option value="approver">Approver</option>
            </select>
            <button type="submit">Register</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </form>
    );
};

export default Register;
